mapper.select:
SELECT id, pwd, memberNum, memberClass 
FROM syl_member 
WHERE id=#{id} AND pwd=#{pwd}


select id 
from syl_member 
WHERE emailAdd=#{emailAdd} and telNum=#{telNum}

mapper.update:
update syl_member set
 pwd=#{newPwd}
 where memberNum=#{memberNum}

update syl_member SET 
telNum=#{telNum}, 
nickName=#{nickName}, 
emailAdd=#{emailAdd} 
where memberNum=#{memberNum}
